import java.util.*;

public class LargestFiveDigitProduct {

	public LargestFiveDigitProduct() {		
		String thousandDigits = "";
		
		final int LENGTH = thousandDigits.length();
		
		List<Integer> digitHolder = new LinkedList<Integer>();

		
		for(int i = 1; i <= LENGTH; i++) {
			digitHolder.add(Integer.parseInt(thousandDigits.substring(i - 1, i), 10));
			System.out.println(digitHolder.get(digitHolder.size() - 1));
		}
	
		double max = 0, product;
		int currentIndex = 0;
		
		while(currentIndex < LENGTH - 5) {			
			product = findProduct(currentIndex, digitHolder);
			if(product > max) {
				max = product;
			}
			
			currentIndex++;
		}
		
		System.out.println("The largest product of five consecutive numbers is " + max);
		
	}
	
	/**
	 * Finds the product of five consecutive numbers in the sequence
	 * @param index --> the beginning index, method stops when index is greater than
	 * the size of the stack minus five.
	 * @return product --> returns the product of the five digits
	 */
	public double findProduct(int index, List<Integer> digitHolder) {
		double product = 1;
		
		for(int i = 0; i < 5; i++) {
			product *= digitHolder.get(index + i);
		}		
		return product;		
	}
}
